## 手撕vue3响应式代理

```javascript
// Vue3 响应式系统核心实现

// 1. 依赖收集相关
let activeEffect = null; // 当前正在执行的副作用函数
const targetMap = new WeakMap(); // 存储依赖关系：target -> key -> effects

// 2. effect 函数：注册副作用
function effect(fn) {
  const effectFn = () => {
    // 清理之前的依赖
    cleanup(effectFn);
    // 设置当前活动的 effect
    activeEffect = effectFn;
    // 执行副作用函数
    const result = fn();
    // 重置
    activeEffect = null;
    return result;
  };

  // 存储与该 effect 相关的依赖
  effectFn.deps = [];

  // 立即执行一次
  effectFn();

  return effectFn;
}

// 清理依赖
function cleanup(effectFn) {
  const deps = effectFn.deps;
  if (deps.length) {
    for (let i = 0; i < deps.length; i++) {
      deps[i].delete(effectFn);
    }
    deps.length = 0;
  }
}

// 3. track 函数：收集依赖
function track(target, key) {
  // 没有活动的 effect，不需要收集
  if (!activeEffect) return;

  // 获取 depsMap：key -> effects
  let depsMap = targetMap.get(target);
  if (!depsMap) {
    targetMap.set(target, (depsMap = new Map()));
  }

  // 获取 dep：Set<effects>
  let dep = depsMap.get(key);
  if (!dep) {
    depsMap.set(key, (dep = new Set()));
  }

  // 添加当前 effect 到依赖集合
  if (!dep.has(activeEffect)) {
    dep.add(activeEffect);
    // 双向关联，方便清理
    activeEffect.deps.push(dep);
  }
}

// 4. trigger 函数：触发更新
function trigger(target, key) {
  const depsMap = targetMap.get(target);
  if (!depsMap) return;

  const dep = depsMap.get(key);
  if (!dep) return;

  // 创建新的 Set 避免无限循环
  const effectsToRun = new Set(dep);

  // 执行所有相关的 effect
  effectsToRun.forEach(effectFn => {
    // 避免无限递归
    if (effectFn !== activeEffect) {
      effectFn();
    }
  });
}

// 5. reactive 函数：创建响应式对象
function reactive(target) {
  // 只对对象类型进行代理
  if (typeof target !== 'object' || target === null) {
    return target;
  }

  // 创建代理
  return new Proxy(target, {
    get(target, key, receiver) {
      // 收集依赖
      track(target, key);

      // 返回值，注意使用 Reflect 保证正确的 this 指向
      const result = Reflect.get(target, key, receiver);

      // 深度响应式
      if (typeof result === 'object' && result !== null) {
        return reactive(result);
      }

      return result;
    },

    set(target, key, value, receiver) {
      // 获取旧值
      const oldValue = target[key];

      // 设置新值
      const result = Reflect.set(target, key, value, receiver);

      // 只有值真正改变时才触发更新
      if (oldValue !== value) {
        trigger(target, key);
      }

      return result;
    },

    // 处理删除操作
    deleteProperty(target, key) {
      const hadKey = Object.prototype.hasOwnProperty.call(target, key);
      const result = Reflect.deleteProperty(target, key);

      if (hadKey && result) {
        trigger(target, key);
      }

      return result;
    }
  });
}

// 6. ref 函数：创建基本类型的响应式
function ref(value) {
  return new RefImpl(value);
}

class RefImpl {
  constructor(value) {
    this._value = convert(value);
    this._rawValue = value;
  }

  get value() {
    track(this, 'value');
    return this._value;
  }

  set value(newValue) {
    if (newValue !== this._rawValue) {
      this._rawValue = newValue;
      this._value = convert(newValue);
      trigger(this, 'value');
    }
  }
}

// 转换函数：对象转为响应式，基本类型保持不变
function convert(value) {
  return typeof value === 'object' && value !== null 
    ? reactive(value) 
    : value;
}

// 7. computed 函数：计算属性
function computed(getter) {
  let value;
  let dirty = true; // 脏值标记，用于缓存

  const effectFn = effect(() => {
    value = getter();
  });

  const obj = {
    get value() {
      if (dirty) {
        value = effectFn();
        dirty = false;
      }
      // 收集依赖
      track(obj, 'value');
      return value;
    }
  };

  return obj;
}

// ============ 使用示例 ============

// 示例1：reactive 基本使用
const state = reactive({
  count: 0,
  user: {
    name: 'Simon',
    age: 21
  }
});

// 注册副作用
effect(() => {
  console.log('count changed:', state.count);
});

// 修改会触发副作用重新执行
state.count++; // 输出: count changed: 1

// 示例2：ref 使用
const message = ref('Hello');

effect(() => {
  console.log('message:', message.value);
});

message.value = 'World'; // 输出: message: World

// 示例3：computed 使用
const doubleCount = computed(() => state.count * 2);

effect(() => {
  console.log('double count:', doubleCount.value);
});

state.count = 5; // 输出: double count: 10
```

